import React from "react";
import "./ARViewer.css";

export default function ARViewer({ modelUrl, poster }) {
  if (!modelUrl) {
    return (
      <div className="ar-card">
        <p>لا يوجد نموذج ثلاثي الأبعاد لهذا الموقع بعد.</p>
      </div>
    );
  }

  return (
    <div className="ar-card">
      <model-viewer
        src={modelUrl}
        ar
        ar-modes="webxr scene-viewer quick-look"
        camera-controls
        poster={poster || ""}
        shadow-intensity="1"
        auto-rotate
        style={{ width: "100%", height: "200px", borderRadius: "16px" }}
      ></model-viewer>
    </div>
  );
}
