import React, { useState, useEffect } from "react";
import axios from "axios";
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Pagination } from "swiper";
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";
import ARViewer from "./ARViewer";
import "./PalestinianReality.css";

export default function PalestinianReality() {
  const [newsItems, setNewsItems] = useState([]);

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const response = await axios.get(
          "http://localhost:8000/api/news/palestinian_reality"
        );
        setNewsItems(response.data);
      } catch (error) {
        console.error("Error fetching news:", error);
      }
    };
    fetchNews();
  }, []);

  return (
    <section className="palestinian-reality">
      <h2 className="pr-header">الواقع الفلسطيني اليوم</h2>

      <Swiper
        modules={[Navigation, Pagination]}
        spaceBetween={20}
        slidesPerView={3}
        navigation
        pagination={{ clickable: true }}
        breakpoints={{
          320: { slidesPerView: 1 },
          640: { slidesPerView: 2 },
          1024: { slidesPerView: 3 },
        }}
        className="pr-carousel"
      >
        {newsItems.map((item, idx) => (
          <SwiperSlide key={idx}>
            <div className="pr-carousel-item">
              {/* نموذج ثلاثي الأبعاد أو أيقونة افتراضية */}
              <ARViewer modelUrl={item.modelUrl} poster={item.poster} />

              <div className="content">
                <h3>{item.title}</h3>
                <p>{item.content.substring(0, 100)}...</p>
                <div className="footer">
                  <span>
                    <i className="fas fa-users"></i> 2.3 مليون متأثر
                  </span>
                  <button>اقرأ التقرير</button>
                </div>
              </div>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </section>
  );
}
