import React, { useEffect, useState } from "react";
import axios from "axios";

export default function PalestinianReality() {
  const [newsItems, setNewsItems] = useState([]);

  useEffect(() => {
    (async () => {
      try {
        const { data } = await axios.get(
          "http://localhost:8000/api/news/palestinian_reality"
        );
        setNewsItems(data || []);
      } catch (e) {
        /* صامت */
      }
    })();
  }, []);

  const regions = [
    {
      title: "الضفة الغربية",
      color: "linear-gradient(135deg,#10b981,#059669)",
      stats: {
        population: "3.2 مليون",
        unemployment: "26%",
        settlements: "132",
      },
      updates: "اعتقالات في نابلس وجنين",
    },
    {
      title: "قطاع غزة",
      color: "linear-gradient(135deg,#ef4444,#b91c1c)",
      stats: { population: "2.3 مليون", unemployment: "47%", years: "17 سنة" },
      updates: "أزمة كهرباء ومياه مستمرة",
    },
    {
      title: "فلسطين 48",
      color: "linear-gradient(135deg,#3b82f6,#1d4ed8)",
      stats: {
        population: "1.9 مليون",
        unemployment: "18%",
        demolitions: "مستمر",
      },
      updates: "قوانين تمييزية وعنصرية",
    },
    {
      title: "الشتات",
      color: "linear-gradient(135deg,#8b5cf6,#6d28d9)",
      stats: { refugees: "6.4 مليون", countries: "58 دولة", camps: "68 مخيم" },
      updates: "مطلب أساسي وحق مقدس",
    },
  ];

  return (
    <section className="page palestinian-reality">
      <div className="pr-header">
        <div>
          <h2>الواقع الفلسطيني اليوم</h2>
          <p>تغطية شاملة للأوضاع السياسية والمعيشية في فلسطين والشتات</p>
        </div>
        <div className="pr-actions">
          <span className="live">
            <i className="fas fa-globe"></i> تحديث مستمر
          </span>
          <button className="add">
            <i className="fas fa-plus"></i> تقرير جديد
          </button>
        </div>
      </div>

      <div className="pr-regions">
        {regions.map((r, i) => (
          <div className="pr-card" key={i}>
            <div className="pr-card-header" style={{ background: r.color }}>
              <span className="flag">🇵🇸</span>
              <h3>{r.title}</h3>
              <p className="muted">
                {r.title === "الشتات"
                  ? "حول العالم"
                  : r.title === "قطاع غزة"
                  ? "تحت الحصار"
                  : "11 محافظة"}
              </p>
            </div>
            <div className="pr-card-body">
              <div className="stats">
                {Object.entries(r.stats).map(([k, v], idx) => (
                  <div className="row" key={idx}>
                    <span className="label">
                      {k === "population" && "السكان"}
                      {k === "unemployment" && "البطالة"}
                      {k === "settlements" && "المستوطنات"}
                      {k === "years" && "سنوات الحصار"}
                      {k === "demolitions" && "هدم المنازل"}
                      {k === "refugees" && "اللاجئون"}
                      {k === "countries" && "الدول المضيفة"}
                      {k === "camps" && "مخيمات اللجوء"}
                    </span>
                    <span className="value">{v}</span>
                  </div>
                ))}
              </div>
              <div className="update">
                <strong>
                  {r.title === "الشتات" ? "حق العودة:" : "آخر التطورات:"}
                </strong>
                <p>{r.updates}</p>
              </div>
              <button className="full">
                {r.title === "الشتات"
                  ? "قصص الشتات"
                  : r.title === "قطاع غزة"
                  ? "تقارير الحصار"
                  : "تقارير مفصلة"}
              </button>
            </div>
          </div>
        ))}
      </div>

      <div className="pr-carousel">
        {(newsItems || []).slice(0, 5).map((item, i) => (
          <div className="pr-carousel-item" key={i}>
            <div className="image">
              <i className="fas fa-home"></i>
              <span className="badge">عاجل</span>
              <span className="time">منذ ساعتين</span>
            </div>
            <div className="content">
              <h3>{item.title}</h3>
              <p>{(item.content || "").substring(0, 100)}...</p>
              <div className="footer">
                <span>
                  <i className="fas fa-users"></i> 2.3 مليون متأثر
                </span>
                <button>اقرأ التقرير</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}
