import React from "react";
import { Link, useLocation } from "react-router-dom";

const Sidebar = () => {
  const location = useLocation();

  const menuItems = [
    {
      icon: "fa-home",
      label: "ุงูุฑุฆูุณูุฉ",
      path: "/",
      active: location.pathname === "/",
    },
    {
      icon: "fa-flag",
      label: "ุงููุงูุน ุงูููุณุทููู",
      path: "/palestinian-reality",
      badge: "๐ต๐ธ",
    },
    {
      icon: "fa-landmark",
      label: "ูุดุงุฑูุน ุฅุญูุงุก ุงูุชุฑุงุซ",
      path: "/heritage",
      badge: "ุฌุฏูุฏ",
    },
    {
      icon: "fa-vr-cardboard",
      label: "ุบุฑูุฉ ุงููุงูุน ุงูุงูุชุฑุงุถู",
      path: "/vr-room",
    },
    { icon: "fa-robot", label: "ูุณุงุนุฏ ุงูุฐูุงุก ุงูุงุตุทูุงุนู", path: "/ai-helper" },
    { icon: "fa-newspaper", label: "ุงูุฃุฎุจุงุฑ ูุงูุชูุงุฑูุฑ", path: "/news" },
    {
      icon: "fa-microphone",
      label: "ุงูุฎุฏูุงุช ุงูุตุญููุฉ",
      path: "/press-services",
    },
    { icon: "fa-users", label: "ุฅุฏุงุฑุฉ ุงูุนุงูููู", path: "/staff" },
    { icon: "fa-chart-line", label: "ุงููุงููุฉ", path: "/finance" },
    { icon: "fa-archive", label: "ุงูููุชุจุฉ ุงูุฅุนูุงููุฉ", path: "/media" },
    { icon: "fa-graduation-cap", label: "ุงูุชุฏุฑูุจ ูุงูุชุทููุฑ", path: "/training" },
    { icon: "fa-building", label: "ุงููุคุณุณุงุช ูุงูุฌูุงุช", path: "/institutions" },
    { icon: "fa-cog", label: "ุงูุฅุนุฏุงุฏุงุช", path: "/settings" },
  ];

  return (
    <aside className="fixed right-0 top-20 h-full w-80 bg-white/90 backdrop-blur-sm shadow-xl border-l-2 border-amber-200 overflow-y-auto">
      <div className="p-6">
        <nav className="space-y-2">
          {menuItems.map((item, index) => (
            <Link
              key={index}
              to={item.path}
              className={`sidebar-item flex items-center space-x-3 space-x-reverse p-4 rounded-lg ${
                item.active
                  ? "bg-amber-50 text-amber-800"
                  : "text-gray-700 hover:text-amber-800"
              }`}
            >
              <i
                className={`fas ${item.icon} text-xl ${
                  item.active ? "text-amber-600" : ""
                }`}
              ></i>
              <span className="font-semibold">{item.label}</span>
              {item.badge && (
                <span
                  className={`mr-auto px-2 py-1 rounded-full text-xs ${
                    item.badge === "๐ต๐ธ"
                      ? "bg-red-100 text-red-800"
                      : "bg-green-100 text-green-800"
                  }`}
                >
                  {item.badge}
                </span>
              )}
            </Link>
          ))}
        </nav>
      </div>
    </aside>
  );
};

export default Sidebar;
