import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import "./css/PalestinianReality.css";

const PalestinianReality = () => {
  const [newsItems, setNewsItems] = useState([]);
  const carouselRef = useRef(null);

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const response = await axios.get(
          "http://localhost:8000/api/news/palestinian_reality"
        );
        setNewsItems(response.data.slice(0, 12));
      } catch (error) {
        console.error("Error fetching news:", error);
      }
    };
    fetchNews();
  }, []);

  // Carousel auto-scroll
  useEffect(() => {
    const interval = setInterval(() => {
      if (carouselRef.current) {
        carouselRef.current.scrollBy({ left: 300, behavior: "smooth" });
        if (
          carouselRef.current.scrollLeft + carouselRef.current.offsetWidth >=
          carouselRef.current.scrollWidth
        ) {
          carouselRef.current.scrollTo({ left: 0, behavior: "smooth" });
        }
      }
    }, 4000);
    return () => clearInterval(interval);
  }, []);

  const scrollLeft = () => {
    if (carouselRef.current)
      carouselRef.current.scrollBy({ left: -300, behavior: "smooth" });
  };

  const scrollRight = () => {
    if (carouselRef.current)
      carouselRef.current.scrollBy({ left: 300, behavior: "smooth" });
  };

  const regions = [
    {
      title: "الضفة الغربية",
      color: "#10B981",
      stats: { السكان: "3.2M", البطالة: "26%", المستوطنات: "132" },
      updates: "اعتقالات في نابلس وجنين",
    },
    {
      title: "قطاع غزة",
      color: "#EF4444",
      stats: { السكان: "2.3M", البطالة: "47%", سنوات: "17" },
      updates: "أزمة كهرباء ومياه مستمرة",
    },
    {
      title: "فلسطين 48",
      color: "#3B82F6",
      stats: { السكان: "1.9M", البطالة: "18%", الهدم: "مستمر" },
      updates: "قوانين تمييزية وعنصرية",
    },
    {
      title: "الشتات",
      color: "#8B5CF6",
      stats: { اللاجئون: "6.4M", الدول: "58", المخيمات: "68" },
      updates: "مطلب أساسي وحق مقدس",
    },
  ];

  return (
    <section className="palestinian-reality">
      <h2 className="pr-title">الواقع الفلسطيني اليوم</h2>

      {/* مناطق فلسطين */}
      <div className="pr-regions">
        {regions.map((region, idx) => (
          <div key={idx} className="pr-card">
            <div
              className="pr-card-header"
              style={{ background: region.color }}
            >
              <h3>{region.title}</h3>
            </div>
            <div className="pr-card-body">
              {Object.entries(region.stats).map(([key, value], i) => (
                <div key={i} className="pr-card-stat">
                  <span>{key}</span>
                  <span>{value}</span>
                </div>
              ))}
              <div className="pr-card-updates">
                <strong>آخر التطورات:</strong> {region.updates}
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Carousel الأخبار */}
      <h2 className="pr-title mt-8">أخبار وتقارير حية</h2>
      <div className="pr-carousel-wrapper">
        <button className="pr-carousel-btn left" onClick={scrollLeft}>
          &#10094;
        </button>
        <div className="pr-carousel" ref={carouselRef}>
          {newsItems.map((item, idx) => (
            <div key={idx} className="pr-card">
              <div className="pr-card-image">
                <i className="fas fa-newspaper"></i>
                <span className="pr-badge">عاجل</span>
              </div>
              <div className="pr-card-content">
                <h3>{item.title}</h3>
                <p>{item.content.substring(0, 120)}...</p>
                <div className="pr-card-footer">
                  <span>
                    <i className="fas fa-users"></i> 2.3 مليون متأثر
                  </span>
                  <button>اقرأ التقرير</button>
                </div>
              </div>
            </div>
          ))}
        </div>
        <button className="pr-carousel-btn right" onClick={scrollRight}>
          &#10095;
        </button>
      </div>
    </section>
  );
};

export default PalestinianReality;
