import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import "./PalestinianReality.css";

const PalestinianReality = () => {
  const [newsItems, setNewsItems] = useState([]);
  const carouselRef = useRef(null);

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const response = await axios.get(
          "http://localhost:8000/api/news/palestinian_reality"
        );
        setNewsItems(response.data);
      } catch (error) {
        console.error("Error fetching news:", error);
      }
    };
    fetchNews();
  }, []);

  // تمرير تلقائي كل 4 ثواني
  useEffect(() => {
    const interval = setInterval(() => {
      if (carouselRef.current) {
        const first = carouselRef.current.firstChild;
        carouselRef.current.appendChild(first);
      }
    }, 4000);
    return () => clearInterval(interval);
  }, [newsItems]);

  const scrollNext = () => {
    if (carouselRef.current) {
      const first = carouselRef.current.firstChild;
      carouselRef.current.appendChild(first);
    }
  };

  const scrollPrev = () => {
    if (carouselRef.current) {
      const last = carouselRef.current.lastChild;
      carouselRef.current.prepend(last);
    }
  };

  return (
    <section className="palestinian-reality">
      <h2 className="pr-title">التقارير الحية</h2>
      <div className="pr-carousel-container">
        <button className="carousel-btn prev" onClick={scrollPrev}>
          &#10094;
        </button>
        <div className="pr-carousel" ref={carouselRef}>
          {newsItems.slice(0, 10).map((item, index) => (
            <div key={index} className="pr-carousel-item">
              <div className="image">
                <i className="fas fa-newspaper"></i>
                <span className="badge">عاجل</span>
                <span className="time">منذ ساعتين</span>
              </div>
              <div className="content">
                <h3>{item.title}</h3>
                <p>{item.content.substring(0, 100)}...</p>
                <div className="footer">
                  <span>
                    <i className="fas fa-users"></i> 2.3 مليون متأثر
                  </span>
                  <button>اقرأ التقرير</button>
                </div>
              </div>
            </div>
          ))}
        </div>
        <button className="carousel-btn next" onClick={scrollNext}>
          &#10095;
        </button>
      </div>
    </section>
  );
};

export default PalestinianReality;
