import React, { useEffect, useRef, useState } from "react";

export default function Carousel({ items, auto = true, interval = 4000 }) {
  const [index, setIndex] = useState(0);
  const wrapRef = useRef(null);

  useEffect(() => {
    if (!auto || items.length <= 1) return;
    const t = setInterval(
      () => setIndex((i) => (i + 1) % items.length),
      interval
    );
    return () => clearInterval(t);
  }, [auto, items.length, interval]);

  const goto = (i) => setIndex((i + items.length) % items.length);

  return (
    <div className="carousel2" ref={wrapRef}>
      <button
        className="nav prev"
        onClick={() => goto(index - 1)}
        aria-label="السابق"
      >
        <i className="fas fa-chevron-right"></i>
      </button>

      <div
        className="track"
        style={{ transform: `translateX(-${index * 100}%)` }}
      >
        {items.map((n, i) => (
          <div className="slide" key={i}>
            {typeof n === "string" ? <div className="slide-text">{n}</div> : n}
          </div>
        ))}
      </div>

      <button
        className="nav next"
        onClick={() => goto(index + 1)}
        aria-label="التالي"
      >
        <i className="fas fa-chevron-left"></i>
      </button>

      <div className="dots">
        {items.map((_, i) => (
          <button
            key={i}
            className={i === index ? "dot active" : "dot"}
            onClick={() => goto(i)}
          />
        ))}
      </div>
    </div>
  );
}
