import React from "react";
import { NavLink } from "react-router-dom";

const items = [
  { icon: "fa-home", label: "الرئيسية", to: "/" },
  { icon: "fa-flag", label: "الواقع الفلسطيني", to: "/palestinian-reality" },
  {
    icon: "fa-landmark",
    label: "مشاريع إحياء التراث",
    to: "/heritage-projects",
  },
  { icon: "fa-vr-cardboard", label: "غرفة الواقع الافتراضي", to: "/vr-room" },
  { icon: "fa-robot", label: "مساعد الذكاء الاصطناعي", to: "/ai-helper" },
  { icon: "fa-newspaper", label: "الأخبار والتقارير", to: "/news" },
  { icon: "fa-microphone", label: "الخدمات الصحفية", to: "/press-services" },
  { icon: "fa-users", label: "إدارة العاملين", to: "/staff-management" },
  { icon: "fa-chart-line", label: "المالية", to: "/finance" },
  { icon: "fa-archive", label: "المكتبة الإعلامية", to: "/media-library" },
  { icon: "fa-graduation-cap", label: "التدريب والتطوير", to: "/training" },
  { icon: "fa-building", label: "المؤسسات والجهات", to: "/institutions" },
  { icon: "fa-cog", label: "الإعدادات", to: "/settings" },
];

export default function Sidebar() {
  return (
    <aside className="sidebar">
      <nav className="side-nav">
        {items.map((it) => (
          <NavLink
            key={it.to}
            to={it.to}
            className={({ isActive }) =>
              "side-item" + (isActive ? " active" : "")
            }
          >
            <i className={`fas ${it.icon}`}></i>
            <span>{it.label}</span>
          </NavLink>
        ))}
      </nav>
    </aside>
  );
}
