import React, { useState, useEffect } from "react";
import axios from "axios";

const Sidebar = () => {
  const [aiMessages, setAiMessages] = useState([
    {
      id: 1,
      text: "مرحباً! أنا مساعدك الإعلامي الذكي. يمكنني مساعدتك في تحليل الأخبار وكتابة التقارير عن التراث الفلسطيني والنابلسي.",
      isUser: false,
    },
  ]);
  const [inputMessage, setInputMessage] = useState("");
  const [news, setNews] = useState([]);

  const sendAIMessage = () => {
    if (!inputMessage.trim()) return;
    const userMessage = { id: Date.now(), text: inputMessage, isUser: true };
    setAiMessages((prev) => [...prev, userMessage]);
    setInputMessage("");

    setTimeout(() => {
      const aiResponses = [
        "تحليل الذكاء الاصطناعي يشير إلى أن الاكتشاف الأثري في نابلس يعكس عمق التاريخ الفلسطيني.",
        "مهرجان التراث النابلسي يعزز الهوية الثقافية الفلسطينية.",
        "نابلس تحتل مكانة مركزية في الخريطة الثقافية الفلسطينية.",
        "التراث النابلسي يواجه تحديات الحفظ والتوثيق.",
      ];
      const aiMessage = {
        id: Date.now() + 1,
        text: aiResponses[Math.floor(Math.random() * aiResponses.length)],
        isUser: false,
      };
      setAiMessages((prev) => [...prev, aiMessage]);
    }, 1000);
  };

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const response = await axios.get(
          "https://api.worldnewsapi.com/search-news",
          {
            params: {
              "source-country": "ps",
              language: "ar",
              apiKey: "06ce6303812b4f869ec43c92a13b55c3",
            },
          }
        );
        setNews(response.data.articles.slice(0, 6));
      } catch (error) {
        console.error("فشل في جلب الأخبار:", error);
      }
    };
    fetchNews();
  }, []);

  const showLiveLocation = (location, description) => {
    alert(
      `📍 موقع مباشر: ${location}\n\n${description}\n\nالمراسلون النشطون: 2\nجودة الإشارة: ممتازة\nعدد المشاهدين: 15,420\n\nهل تريد مشاهدة البث المباشر؟`
    );
  };

  return (
    <section className="py-8 bg-gradient-to-br from-gray-100 to-blue-50">
      <div className="container mx-auto px-6">
        {/* Header: Banner | Title | Logo */}
        <div className="flex items-center justify-between mb-8">
          {/* البانر على اليسار */}
          <div className="banner">
            <img src="/path/to/banner.png" alt="Banner" className="h-12" />
          </div>

          {/* العنوان */}
          <h1 className="text-4xl font-bold text-gray-900 text-center">
            اسم المنصة
          </h1>

          {/* اللوجو على اليمين */}
          <div className="logo">
            <img src="/path/to/logo.png" alt="Logo" className="h-12" />
          </div>
        </div>

        <h2 className="text-4xl font-bold text-gray-900 mb-6 text-center">
          لوحة المعلومات المباشرة
        </h2>
        <div className="grid lg:grid-cols-4 gap-6 mb-6">
          {/* Sidebar */}
          <div className="lg:col-span-1 space-y-6">
            {/* AI Chat */}
            <div className="bg-white rounded-lg shadow-lg p-4 ai-glow">
              <h3 className="font-bold text-gray-900 mb-3 flex items-center">
                <span className="ml-2">🤖</span> المساعد الذكي
              </h3>
              <div className="bg-gray-50 rounded-lg p-3 h-64 overflow-y-auto mb-3">
                {aiMessages.map((msg) => (
                  <div
                    key={msg.id}
                    className={`mb-2 flex ${
                      msg.isUser ? "justify-end" : "justify-start"
                    }`}
                  >
                    <span
                      className={`inline-block px-3 py-2 rounded max-w-xs ${
                        msg.isUser
                          ? "bg-gray-600 text-white"
                          : "bg-blue-500 text-white"
                      }`}
                    >
                      {msg.text}
                    </span>
                  </div>
                ))}
              </div>
              <div className="flex">
                <input
                  type="text"
                  value={inputMessage}
                  onChange={(e) => setInputMessage(e.target.value)}
                  onKeyPress={(e) => e.key === "Enter" && sendAIMessage()}
                  placeholder="اسأل عن الأخبار..."
                  className="flex-1 border rounded-r-lg px-2 py-1 text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
                <button
                  onClick={sendAIMessage}
                  className="bg-blue-500 text-white px-3 py-1 rounded-l-lg hover:bg-blue-600 transition-colors text-sm"
                >
                  إرسال
                </button>
              </div>
            </div>

            {/* Live Map */}
            <div className="bg-white rounded-lg shadow-lg p-4">
              <h3 className="font-bold text-gray-900 mb-3 flex items-center">
                <span className="ml-2">📍</span> الخريطة الحية
              </h3>
              <div className="bg-green-300 rounded-lg h-48 relative overflow-hidden">
                {/* Marker 1 */}
                <div
                  className="absolute top-1/3 left-1/2 transform -translate-x-1/2 -translate-y-1/2 cursor-pointer"
                  onClick={() =>
                    showLiveLocation(
                      "المسجد الأقصى",
                      "بث مباشر من القدس الشريف"
                    )
                  }
                >
                  <div className="bg-red-600 text-white rounded-full w-8 h-8 flex items-center justify-center shadow-lg pulse-live">
                    🕌
                  </div>
                </div>
                {/* Marker 2 */}
                <div
                  className="absolute top-2/3 left-1/3 transform -translate-x-1/2 -translate-y-1/2 cursor-pointer"
                  onClick={() =>
                    showLiveLocation(
                      "نابلس",
                      "تغطية مباشرة من موقع الاكتشاف الأثري"
                    )
                  }
                >
                  <div className="bg-blue-500 text-white rounded-full w-8 h-8 flex items-center justify-center shadow-lg pulse-live">
                    🏛️
                  </div>
                </div>
                {/* Marker 3 */}
                <div
                  className="absolute top-1/2 left-2/3 transform -translate-x-1/2 -translate-y-1/2 cursor-pointer"
                  onClick={() => showLiveLocation("جبل جرزيم", "مراسل ميداني")}
                >
                  <div className="bg-green-600 text-white rounded-full w-6 h-6 flex items-center justify-center shadow-lg">
                    ⛰️
                  </div>
                </div>
              </div>
            </div>

            {/* News Ticker */}
            <div className="bg-white rounded-lg shadow-lg p-4 overflow-hidden">
              <h3 className="font-bold text-gray-900 mb-2 flex items-center">
                <span className="ml-2">📰</span> الأخبار المتحركة
              </h3>
              <div className="overflow-hidden whitespace-nowrap">
                <div className="inline-block animate-scroll">
                  {news.length > 0 ? (
                    news.map((n, idx) => (
                      <span
                        key={idx}
                        className="mx-4 text-sm text-gray-800 font-medium"
                      >
                        {n.title}
                      </span>
                    ))
                  ) : (
                    <span className="text-sm text-gray-500">
                      جاري تحميل الأخبار...
                    </span>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Sidebar;
