import React, { useState, useEffect } from "react";
import axios from "axios";
import Modal3D from "./Modal3D";
import "./PalestinianReality.css";

const PalestinianReality = () => {
  const [newsItems, setNewsItems] = useState([]);
  const [selectedModel, setSelectedModel] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    axios
      .get("http://localhost:8000/api/news/palestinian_reality")
      .then((res) => setNewsItems(res.data))
      .catch((err) => console.error(err));
  }, []);

  const openModal = (url) => {
    setSelectedModel(url);
    setIsModalOpen(true);
  };
  const closeModal = () => {
    setSelectedModel(null);
    setIsModalOpen(false);
  };

  return (
    <section className="palestinian-reality">
      <h2 className="pr-title">الواقع الفلسطيني اليوم</h2>
      <div className="pr-carousel">
        {newsItems.slice(0, 5).map((item, index) => (
          <div
            key={index}
            className="pr-carousel-item"
            onClick={() => openModal(item.modelUrl)}
          >
            <div className="image">
              <i className="fas fa-home"></i>
              <span className="badge">عاجل</span>
              <span className="time">منذ ساعتين</span>
            </div>
            <div className="content">
              <h3>{item.title}</h3>
              <p>{item.content.substring(0, 100)}...</p>
            </div>
          </div>
        ))}
      </div>
      <Modal3D
        isOpen={isModalOpen}
        onClose={closeModal}
        modelUrl={selectedModel}
      />
    </section>
  );
};

export default PalestinianReality;
