import React, { useState, useEffect } from "react";
import axios from "axios";
import "./PalestinianReality.css";

const PalestinianReality = () => {
  const [newsItems, setNewsItems] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [visibleCount, setVisibleCount] = useState(3); // عدد العناصر المرئية على سطح المكتب

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const response = await axios.get(
          "http://localhost:8000/api/news/palestinian_reality"
        );
        setNewsItems(response.data.slice(0, 10));
      } catch (error) {
        console.error("Error fetching news:", error);
      }
    };
    fetchNews();
  }, []);

  // تحديد عدد العناصر حسب حجم الشاشة
  useEffect(() => {
    const updateVisibleCount = () => {
      if (window.innerWidth < 640) setVisibleCount(1);
      else if (window.innerWidth < 1024) setVisibleCount(2);
      else setVisibleCount(3);
    };
    updateVisibleCount();
    window.addEventListener("resize", updateVisibleCount);
    return () => window.removeEventListener("resize", updateVisibleCount);
  }, []);

  // تمرير تلقائي
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prev) =>
        prev >= newsItems.length - visibleCount ? 0 : prev + 1
      );
    }, 4000);
    return () => clearInterval(interval);
  }, [newsItems, visibleCount]);

  const scrollNext = () => {
    setCurrentIndex((prev) =>
      prev >= newsItems.length - visibleCount ? 0 : prev + 1
    );
  };

  const scrollPrev = () => {
    setCurrentIndex((prev) =>
      prev <= 0 ? newsItems.length - visibleCount : prev - 1
    );
  };

  return (
    <section className="palestinian-reality">
      <h2 className="pr-title">التقارير الحية</h2>

      <div className="pr-carousel-container">
        <button className="carousel-btn prev" onClick={scrollPrev}>
          &#10094;
        </button>

        <div className="pr-carousel-wrapper">
          <div
            className="pr-carousel"
            style={{
              transform: `translateX(-${(100 / visibleCount) * currentIndex}%)`,
            }}
          >
            {newsItems.map((item, index) => (
              <div
                key={index}
                className="pr-carousel-item"
                style={{ minWidth: `${100 / visibleCount}%` }}
              >
                <div className="image">
                  <i className="fas fa-newspaper"></i>
                  <span className="badge">عاجل</span>
                  <span className="time">منذ ساعتين</span>
                </div>
                <div className="content">
                  <h3>{item.title}</h3>
                  <p>{item.content.substring(0, 100)}...</p>
                  <div className="footer">
                    <span>
                      <i className="fas fa-users"></i> 2.3 مليون متأثر
                    </span>
                    <button>اقرأ التقرير</button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        <button className="carousel-btn next" onClick={scrollNext}>
          &#10095;
        </button>
      </div>
    </section>
  );
};

export default PalestinianReality;
