import Modal3D from "./Modal3D";

export default function PalestinianReality() {
  const [newsItems, setNewsItems] = useState([]);
  const [modalOpen, setModalOpen] = useState(false);
  const [currentModel, setCurrentModel] = useState(null);
  const [currentPoster, setCurrentPoster] = useState(null);

  // عند النقر على أي نموذج
  const openModelModal = (modelUrl, poster) => {
    setCurrentModel(modelUrl);
    setCurrentPoster(poster);
    setModalOpen(true);
  };

  const closeModal = () => setModalOpen(false);

  return (
    <section className="palestinian-reality">
      <h2 className="pr-header">الواقع الفلسطيني اليوم</h2>

      <Swiper
        modules={[Navigation, Pagination]}
        spaceBetween={20}
        slidesPerView={3}
        navigation
        pagination={{ clickable: true }}
        breakpoints={{
          320: { slidesPerView: 1 },
          640: { slidesPerView: 2 },
          1024: { slidesPerView: 3 },
        }}
        className="pr-carousel"
      >
        {newsItems.map((item, idx) => (
          <SwiperSlide key={idx}>
            <div
              className="pr-carousel-item"
              onClick={() => openModelModal(item.modelUrl, item.poster)}
            >
              <ARViewer modelUrl={item.modelUrl} poster={item.poster} />
              <div className="content">
                <h3>{item.title}</h3>
                <p>{item.content.substring(0, 100)}...</p>
                <div className="footer">
                  <span>
                    <i className="fas fa-users"></i> 2.3 مليون متأثر
                  </span>
                  <button>اقرأ التقرير</button>
                </div>
              </div>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>

      <Modal3D
        isOpen={modalOpen}
        onClose={closeModal}
        modelUrl={currentModel}
        poster={currentPoster}
      />
    </section>
  );
}
