    { icon: "fa-landmark", label: "ูุดุงุฑูุน ุฅุญูุงุก ุงูุชุฑุงุซ", badge: "ุฌุฏูุฏ" },
    { icon: "fa-vr-cardboard", label: "ุบุฑูุฉ ุงููุงูุน ุงูุงูุชุฑุงุถู" },
    { icon: "fa-robot", label: "ูุณุงุนุฏ ุงูุฐูุงุก ุงูุงุตุทูุงุนู" },
    { icon: "fa-newspaper", label: "ุงูุฃุฎุจุงุฑ ูุงูุชูุงุฑูุฑ" },
    { icon: "fa-microphone", label: "ุงูุฎุฏูุงุช ุงูุตุญููุฉ" },
    { icon: "fa-users", label: "ุฅุฏุงุฑุฉ ุงูุนุงูููู" },
    { icon: "fa-chart-line", label: "ุงููุงููุฉ" },
    { icon: "fa-archive", label: "ุงูููุชุจุฉ ุงูุฅุนูุงููุฉ" },
    { icon: "fa-graduation-cap", label: "ุงูุชุฏุฑูุจ ูุงูุชุทููุฑ" },
    { icon: "fa-building", label: "ุงููุคุณุณุงุช ูุงูุฌูุงุช" },
    { icon: "fa-cog", label: "ุงูุฅุนุฏุงุฏุงุช" },
  ];

  return (
    <aside className={`sidebar ${isOpen ? "open" : ""}`}>
      <nav className="sidebar-nav">
        {menuItems.map((item, index) => (
          <a
            key={index}
            href="#"
            className={`sidebar-item ${item.active ? "active" : ""}`}
          >
            <i className={`fas ${item.icon}`}></i>
            <span>{item.label}</span>
            {item.badge && (
              <span
                className={`sidebar-badge ${
                  item.badge === "๐ต๐ธ" ? "palestine" : "new"
                }`}
              >
                {item.badge}
              </span>
            )}
          </a>
        ))}
      </nav>
    </aside>
  );
};

export default Sidebar;
