import React, { useEffect, useRef } from "react";
import "./Sidebar.css";

const Sidebar = ({ open, onClose }) => {
  const sidebarRef = useRef();

  const menuItems = [
    { icon: "fa-home", label: "الرئيسية", active: true },
    { icon: "fa-flag", label: "الواقع الفلسطيني", badge: "🇵🇸" },
    { icon: "fa-landmark", label: "مشاريع إحياء التراث", badge: "جديد" },
    { icon: "fa-vr-cardboard", label: "غرفة الواقع الافتراضي" },
    { icon: "fa-robot", label: "مساعد الذكاء الاصطناعي" },
    { icon: "fa-newspaper", label: "الأخبار والتقارير" },
    { icon: "fa-microphone", label: "الخدمات الصحفية" },
    { icon: "fa-users", label: "إدارة العاملين" },
    { icon: "fa-chart-line", label: "المالية" },
    { icon: "fa-archive", label: "المكتبة الإعلامية" },
    { icon: "fa-graduation-cap", label: "التدريب والتطوير" },
    { icon: "fa-building", label: "المؤسسات والجهات" },
    { icon: "fa-cog", label: "الإعدادات" },
  ];

  // Swipe Gesture for mobile
  useEffect(() => {
    let startX = 0;
    const handleTouchStart = (e) => {
      startX = e.touches[0].clientX;
    };
    const handleTouchMove = (e) => {
      const moveX = e.touches[0].clientX;
      if (startX - moveX > 50) onClose(); // Swipe left to close
    };
    const sidebar = sidebarRef.current;
    sidebar.addEventListener("touchstart", handleTouchStart);
    sidebar.addEventListener("touchmove", handleTouchMove);
    return () => {
      sidebar.removeEventListener("touchstart", handleTouchStart);
      sidebar.removeEventListener("touchmove", handleTouchMove);
    };
  }, [onClose]);

  return (
    <>
      <div className={`overlay ${open ? "open" : ""}`} onClick={onClose}></div>
      <aside ref={sidebarRef} className={`sidebar ${open ? "open" : ""}`}>
        <div className="p-6 flex justify-between items-center lg:hidden">
          <h2 className="text-xl font-bold">القائمة</h2>
          <button onClick={onClose}>
            <i className="fas fa-times text-xl"></i>
          </button>
        </div>
        <nav className="space-y-2 p-6">
          {menuItems.map((item, index) => (
            <a
              key={index}
              href="#"
              className={`sidebar-item ${item.active ? "active" : ""}`}
            >
              <i className={`fas ${item.icon}`}></i>
              <span>{item.label}</span>
              {item.badge && (
                <span
                  className={`badge ${item.badge === "🇵🇸" ? "red" : "green"}`}
                >
                  {item.badge}
                </span>
              )}
            </a>
          ))}
        </nav>
      </aside>
    </>
  );
};

export default Sidebar;
