import React from "react";
import Carousel from "../components/Carousel";
import PalestinianReality from "../components/PalestinianReality";

const Home = () => {
  const items = [
    <div key="1" className="hero">
      <h2>مرحبًا بك في أصداء</h2>
      <p>منصة تجمع التراث بالإعلام الحديث</p>
    </div>,
    <div key="2" className="hero alt">
      <h2>تقارير مباشرة</h2>
      <p>متابعة لحظية للأخبار</p>
    </div>,
    <div key="3" className="hero alt2">
      <h2>مشاريع إحياء التراث</h2>
      <p>اكتشف جهود الترميم والحفظ</p>
    </div>,
  ];

  return (
    <section className="page home">
      <Carousel items={items} auto interval={4500} />
      <div className="cards-3">
        <PalestinianReality />
        <div className="mini-card g1">أحدث الأخبار</div>
        <div className="mini-card g2">مشاريع جارية</div>
        <div className="mini-card g3">تقارير وتحليلات</div>
      </div>
    </section>
  );
};

export default Home;
