import React from "react";
import "./Sidebar.css";

const Sidebar = ({ isOpen }) => {
  const menuItems = [
    { icon: "fa-home", label: "ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©", active: true },
    { icon: "fa-flag", label: "ÿßŸÑŸàÿßŸÇÿπ ÿßŸÑŸÅŸÑÿ≥ÿ∑ŸäŸÜŸä", badge: "üáµüá∏" },
    { icon: "fa-landmark", label: "ŸÖÿ¥ÿßÿ±Ÿäÿπ ÿßŸÑÿ™ÿ±ÿßÿ´", badge: "ÿ¨ÿØŸäÿØ" },
    { icon: "fa-vr-cardboard", label: "ÿ∫ÿ±ŸÅÿ© ÿßŸÑŸàÿßŸÇÿπ ÿßŸÑÿßŸÅÿ™ÿ±ÿßÿ∂Ÿä" },
  ];

  return (
    <aside className={`sidebar ${isOpen ? "open" : ""}`}>
      <nav>
        {menuItems.map((item, index) => (
          <a
            key={index}
            href="#"
            className={`sidebar-item ${item.active ? "active" : ""}`}
          >
            <i className={`fas ${item.icon}`}></i>
            <span>{item.label}</span>
            {item.badge && <span className="badge">{item.badge}</span>}
          </a>
        ))}
      </nav>
    </aside>
  );
};

export default Sidebar;
