import React, { useState, useEffect } from "react";
import axios from "axios";

const Sidebar = () => {
  const [aiMessages, setAiMessages] = useState([
    {
      id: 1,
      text: "ŸÖÿ±ÿ≠ÿ®ÿßŸã! ÿ£ŸÜÿß ŸÖÿ≥ÿßÿπÿØŸÉ ÿßŸÑÿ•ÿπŸÑÿßŸÖŸä ÿßŸÑÿ∞ŸÉŸä. ŸäŸÖŸÉŸÜŸÜŸä ŸÖÿ≥ÿßÿπÿØÿ™ŸÉ ŸÅŸä ÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑÿ£ÿÆÿ®ÿßÿ± ŸàŸÉÿ™ÿßÿ®ÿ© ÿßŸÑÿ™ŸÇÿßÿ±Ÿäÿ± ÿπŸÜ ÿßŸÑÿ™ÿ±ÿßÿ´ ÿßŸÑŸÅŸÑÿ≥ÿ∑ŸäŸÜŸä ŸàÿßŸÑŸÜÿßÿ®ŸÑÿ≥Ÿä.",
      isUser: false,
    },
  ]);

  const [inputMessage, setInputMessage] = useState("");
  const [news, setNews] = useState([]);

  const sendAIMessage = () => {
    if (!inputMessage.trim()) return;
    const userMessage = { id: Date.now(), text: inputMessage, isUser: true };
    setAiMessages((prev) => [...prev, userMessage]);
    setInputMessage("");

    setTimeout(() => {
      const aiResponses = [
        "ÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑÿ∞ŸÉÿßÿ° ÿßŸÑÿßÿµÿ∑ŸÜÿßÿπŸä Ÿäÿ¥Ÿäÿ± ÿ•ŸÑŸâ ÿ£ŸÜ ÿßŸÑÿßŸÉÿ™ÿ¥ÿßŸÅ ÿßŸÑÿ£ÿ´ÿ±Ÿä ŸÅŸä ŸÜÿßÿ®ŸÑÿ≥ ŸäÿπŸÉÿ≥ ÿπŸÖŸÇ ÿßŸÑÿ™ÿßÿ±ŸäÿÆ ÿßŸÑŸÅŸÑÿ≥ÿ∑ŸäŸÜŸä.",
        "ŸÖŸáÿ±ÿ¨ÿßŸÜ ÿßŸÑÿ™ÿ±ÿßÿ´ ÿßŸÑŸÜÿßÿ®ŸÑÿ≥Ÿä Ÿäÿπÿ≤ÿ≤ ÿßŸÑŸáŸàŸäÿ© ÿßŸÑÿ´ŸÇÿßŸÅŸäÿ© ÿßŸÑŸÅŸÑÿ≥ÿ∑ŸäŸÜŸäÿ©.",
        "ŸÜÿßÿ®ŸÑÿ≥ ÿ™ÿ≠ÿ™ŸÑ ŸÖŸÉÿßŸÜÿ© ŸÖÿ±ŸÉÿ≤Ÿäÿ© ŸÅŸä ÿßŸÑÿÆÿ±Ÿäÿ∑ÿ© ÿßŸÑÿ´ŸÇÿßŸÅŸäÿ© ÿßŸÑŸÅŸÑÿ≥ÿ∑ŸäŸÜŸäÿ©.",
        "ÿßŸÑÿ™ÿ±ÿßÿ´ ÿßŸÑŸÜÿßÿ®ŸÑÿ≥Ÿä ŸäŸàÿßÿ¨Ÿá ÿ™ÿ≠ÿØŸäÿßÿ™ ÿßŸÑÿ≠ŸÅÿ∏ ŸàÿßŸÑÿ™Ÿàÿ´ŸäŸÇ.",
      ];
      const aiMessage = {
        id: Date.now() + 1,
        text: aiResponses[Math.floor(Math.random() * aiResponses.length)],
        isUser: false,
      };
      setAiMessages((prev) => [...prev, aiMessage]);
    }, 1000);
  };

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const response = await axios.get(
          "https://api.worldnewsapi.com/search-news",
          {
            params: {
              "source-country": "ps",
              language: "ar",
              apiKey: "06ce6303812b4f869ec43c92a13b55c3",
            },
          }
        );
        setNews(response.data.articles.slice(0, 4));
      } catch (error) {
        console.error("ŸÅÿ¥ŸÑ ŸÅŸä ÿ¨ŸÑÿ® ÿßŸÑÿ£ÿÆÿ®ÿßÿ±:", error);
      }
    };
    fetchNews();
  }, []);

  const showLiveLocation = (location, description) => {
    alert(
      `üìç ŸÖŸàŸÇÿπ ŸÖÿ®ÿßÿ¥ÿ±: ${location}\n\n${description}\n\nÿßŸÑŸÖÿ±ÿßÿ≥ŸÑŸàŸÜ ÿßŸÑŸÜÿ¥ÿ∑ŸàŸÜ: 2\nÿ¨ŸàÿØÿ© ÿßŸÑÿ•ÿ¥ÿßÿ±ÿ©: ŸÖŸÖÿ™ÿßÿ≤ÿ©\nÿπÿØÿØ ÿßŸÑŸÖÿ¥ÿßŸáÿØŸäŸÜ: 15,420\n\nŸáŸÑ ÿ™ÿ±ŸäÿØ ŸÖÿ¥ÿßŸáÿØÿ© ÿßŸÑÿ®ÿ´ ÿßŸÑŸÖÿ®ÿßÿ¥ÿ±ÿü`
    );
  };

  return (
    <section className="py-8 bg-gradient-to-br from-gray-100 to-blue-50">
      <div className="container mx-auto px-6">
        <h2 className="text-4xl font-bold text-gray-900 mb-6 text-center">
          ŸÑŸàÿ≠ÿ© ÿßŸÑŸÖÿπŸÑŸàŸÖÿßÿ™ ÿßŸÑŸÖÿ®ÿßÿ¥ÿ±ÿ©
        </h2>
        <div className="grid lg:grid-cols-4 gap-6 mb-6">
          {/* Sidebar */}
          <div className="lg:col-span-1 space-y-6">
            {/* AI Chat */}
            <div className="bg-white rounded-lg shadow-lg p-4 ai-glow">
              <h3 className="font-bold text-gray-900 mb-3 flex items-center">
                <span className="ml-2">ü§ñ</span> ÿßŸÑŸÖÿ≥ÿßÿπÿØ ÿßŸÑÿ∞ŸÉŸä
              </h3>
              <div className="bg-gray-50 rounded-lg p-3 h-64 overflow-y-auto mb-3">
                {aiMessages.map((msg) => (
                  <div
                    key={msg.id}
                    className={`mb-2 flex ${
                      msg.isUser ? "justify-end" : "justify-start"
                    }`}
                  >
                    <span
                      className={`inline-block px-3 py-2 rounded max-w-xs ${
                        msg.isUser
                          ? "bg-gray-600 text-white"
                          : "bg-blue-500 text-white"
                      }`}
                    >
                      {msg.text}
                    </span>
                  </div>
                ))}
              </div>
              <div className="flex">
                <input
                  type="text"
                  value={inputMessage}
                  onChange={(e) => setInputMessage(e.target.value)}
                  onKeyPress={(e) => e.key === "Enter" && sendAIMessage()}
                  placeholder="ÿßÿ≥ÿ£ŸÑ ÿπŸÜ ÿßŸÑÿ£ÿÆÿ®ÿßÿ±..."
                  className="flex-1 border rounded-r-lg px-2 py-1 text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
                <button
                  onClick={sendAIMessage}
                  className="bg-blue-500 text-white px-3 py-1 rounded-l-lg hover:bg-blue-600 transition-colors text-sm"
                >
                  ÿ•ÿ±ÿ≥ÿßŸÑ
