import React, { useState, useEffect } from "react";
import axios from "axios";
import Modal3D from "./Modal3D"; // استيراد المودال
import "./PalestinianReality.css";

const PalestinianReality = () => {
  const [newsItems, setNewsItems] = useState([]);
  const [selectedModel, setSelectedModel] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const response = await axios.get(
          "http://localhost:8000/api/news/palestinian_reality"
        );
        setNewsItems(response.data);
      } catch (error) {
        console.error("Error fetching news:", error);
      }
    };
    fetchNews();
  }, []);

  const handleOpenModal = (modelUrl) => {
    setSelectedModel(modelUrl);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setSelectedModel(null);
    setIsModalOpen(false);
  };

  return (
    <section className="palestinian-reality">
      <div className="pr-header">
        <h2>الواقع الفلسطيني اليوم</h2>
        <p>تغطية شاملة للأوضاع السياسية والمعيشية في فلسطين والشتات</p>
      </div>

      <div className="pr-carousel">
        {newsItems.slice(0, 5).map((item, index) => (
          <div
            key={index}
            className="pr-carousel-item"
            onClick={() => handleOpenModal(item.modelUrl)}
          >
            <div className="image">
              <i className="fas fa-home"></i>
              <span className="badge">عاجل</span>
              <span className="time">منذ ساعتين</span>
            </div>
            <div className="content">
              <h3>{item.title}</h3>
              <p>{item.content.substring(0, 100)}...</p>
              <div className="footer">
                <span>
                  <i className="fas fa-users"></i> 2.3 مليون متأثر
                </span>
                <button>اقرأ التقرير</button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* مودال 3D */}
      <Modal3D
        isOpen={isModalOpen}
        onClose={handleCloseModal}
        modelUrl={selectedModel}
      />
    </section>
  );
};

export default PalestinianReality;
