import React, { useState, useEffect } from "react";
import axios from "axios";
import "./css/PalestinianReality.css";

const PalestinianReality = () => {
  const [newsItems, setNewsItems] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const response = await axios.get(
          "http://localhost:8000/api/news/palestinian_reality"
        );
        setNewsItems(response.data.slice(0, 10));
      } catch (error) {
        console.error("Error fetching news:", error);
      }
    };
    fetchNews();
  }, []);

  // تمرير تلقائي
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prev) => (prev === newsItems.length - 1 ? 0 : prev + 1));
    }, 4000);
    return () => clearInterval(interval);
  }, [newsItems]);

  const scrollNext = () => {
    setCurrentIndex((prev) => (prev === newsItems.length - 1 ? 0 : prev + 1));
  };

  const scrollPrev = () => {
    setCurrentIndex((prev) => (prev === 0 ? newsItems.length - 1 : prev - 1));
  };

  return (
    <section className="palestinian-reality">
      <h2 className="pr-title">التقارير الحية</h2>

      <div className="pr-carousel-container">
        <button className="carousel-btn prev" onClick={scrollPrev}>
          &#10094;
        </button>

        <div className="pr-carousel">
          {newsItems.map((item, index) => (
            <div
              key={index}
              className={`pr-carousel-item ${
                index === currentIndex ? "active" : "inactive"
              }`}
            >
              <div className="image">
                <i className="fas fa-newspaper"></i>
                <span className="badge">عاجل</span>
                <span className="time">منذ ساعتين</span>
              </div>
              <div className="content">
                <h3>{item.title}</h3>
                <p>{item.content.substring(0, 100)}...</p>
                <div className="footer">
                  <span>
                    <i className="fas fa-users"></i> 2.3 مليون متأثر
                  </span>
                  <button>اقرأ التقرير</button>
                </div>
              </div>
            </div>
          ))}
        </div>

        <button className="carousel-btn next" onClick={scrollNext}>
          &#10095;
        </button>
      </div>
    </section>
  );
};

export default PalestinianReality;
