import React, { useState, useEffect } from "react";
import axios from "axios";
import "./PalestinianReality.css"; // ربط ملف CSS الجديد

const PalestinianReality = () => {
  const [newsItems, setNewsItems] = useState([]);

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const response = await axios.get(
          "http://localhost:8000/api/news/palestinian_reality"
        );
        setNewsItems(response.data);
      } catch (error) {
        console.error("Error fetching news:", error);
      }
    };

    fetchNews();
  }, []);

  const regions = [
    {
      title: "الضفة الغربية",
      color: "#16a34a",
      stats: {
        population: "3.2 مليون",
        unemployment: "26%",
        settlements: "132",
      },
      updates: "اعتقالات في نابلس وجنين",
    },
    {
      title: "قطاع غزة",
      color: "#dc2626",
      stats: { population: "2.3 مليون", unemployment: "47%", years: "17 سنة" },
      updates: "أزمة كهرباء ومياه مستمرة",
    },
    {
      title: "فلسطين 48",
      color: "#2563eb",
      stats: {
        population: "1.9 مليون",
        unemployment: "18%",
        demolitions: "مستمر",
      },
      updates: "قوانين تمييزية وعنصرية",
    },
    {
      title: "الشتات",
      color: "#8b5cf6",
      stats: { refugees: "6.4 مليون", countries: "58 دولة", camps: "68 مخيم" },
      updates: "مطلب أساسي وحق مقدس",
    },
  ];

  return (
    <section className="palestinian-reality">
      {/* Header */}
      <div className="pr-header">
        <div className="pr-header-info">
          <h2>الواقع الفلسطيني اليوم</h2>
          <p>تغطية شاملة للأوضاع السياسية والمعيشية في فلسطين والشتات</p>
        </div>
        <div className="pr-header-actions">
          <span className="status">
            <i className="fas fa-globe"></i> تحديث مستمر
          </span>
          <button className="new-report">
            <i className="fas fa-plus"></i> تقرير جديد
          </button>
        </div>
      </div>

      {/* المناطق */}
      <div className="pr-regions">
        {regions.map((region, index) => (
          <div key={index} className="pr-card">
            <div
              className="pr-card-header"
              style={{ backgroundColor: region.color }}
            >
              <div className="flag">🇵🇸</div>
              <h3>{region.title}</h3>
              <p>
                {region.title === "الشتات"
                  ? "حول العالم"
                  : region.title === "قطاع غزة"
                  ? "تحت الحصار"
                  : "11 محافظة"}
              </p>
            </div>
            <div className="pr-card-body">
              {Object.entries(region.stats).map(([key, value], i) => (
                <div key={i} className="stat">
                  <span>
                    {key === "population" && "السكان"}
                    {key === "unemployment" && "البطالة"}
                    {key === "settlements" && "المستوطنات"}
                    {key === "years" && "سنوات الحصار"}
                    {key === "demolitions" && "هدم المنازل"}
                    {key === "refugees" && "اللاجئون"}
                    {key === "countries" && "الدول المضيفة"}
                    {key === "camps" && "مخيمات اللجوء"}
                  </span>
                  <span className="value">{value}</span>
                </div>
              ))}
              <div className="updates">
                <strong>
                  {region.title === "الشتات" ? "حق العودة:" : "آخر التطورات:"}
                </strong>
                <p>{region.updates}</p>
              </div>
              <button className="detail-btn">
                {region.title === "الشتات"
                  ? "قصص الشتات"
                  : region.title === "قطاع غزة"
                  ? "تقارير الحصار"
                  : "تقارير مفصلة"}
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* التقارير الحية */}
      <div className="pr-carousel">
        {newsItems.slice(0, 5).map((item, index) => (
          <div key={index} className="pr-carousel-item">
            <div className="image">
              <i className="fas fa-home"></i>
              <span className="badge">عاجل</span>
              <span className="time">منذ ساعتين</span>
            </div>
            <div className="content">
              <h3>{item.title}</h3>
              <p>{item.content.substring(0, 100)}...</p>
              <div className="footer">
                <span>
                  <i className="fas fa-users"></i> 2.3 مليون متأثر
                </span>
                <button>اقرأ التقرير</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default PalestinianReality;
