    { icon: "fa-newspaper", label: "ุงูุฃุฎุจุงุฑ" },
    { icon: "fa-vr-cardboard", label: "ุงููุงูุน ุงูุงูุชุฑุงุถู" },
    { icon: "fa-flag", label: "ุงููุงูุน ุงูููุณุทููู", badge: "๐ต๐ธ" },
    { icon: "fa-landmark", label: "ูุดุงุฑูุน ุงูุชุฑุงุซ", badge: "ุฌุฏูุฏ" },
    { icon: "fa-robot", label: "ูุณุงุนุฏ ุงูุฐูุงุก ุงูุงุตุทูุงุนู" },
  ];

  return (
    <aside className="sidebar">
      {menuItems.map((item, index) => (
        <div
          key={index}
          className={`sidebar-item ${item.active ? "active" : ""}`}
        >
          <i className={`fas ${item.icon}`}></i>
          <span>{item.label}</span>
          {item.badge && <span className="badge">{item.badge}</span>}
        </div>
      ))}
    </aside>
  );
};

export default Sidebar;
